        >
          {children}
          {!children && <Info className="w-3 h-3 text-gray-400 hover:text-gray-600" />}
        </div>
        {showTooltip && (
          <div className="absolute z-50 top-full left-1/2 transform -translate-x-1/2 mt-2 px-4 py-3 bg-gray-900 text-white text-xs rounded-lg shadow-lg max-w-md">
            <div className="font-medium mb-2">Description:</div>
            <div className="whitespace-pre-wrap leading-relaxed">{formula}</div>
            <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 w-0 h-0 border-l-4 border-r-4 border-b-4 border-transparent border-b-gray-900"></div>
          </div>
        )}
      </div>
    );
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
