      // Business EVA
      const businessEVA = calculateBusinessEVA(scenario.ebitda, businessCapitalInvested, scenario.wacc / 100);

      // Log Business EVA calculation
      if (DEBUG_METRICS) {
        logMetricCalculation(
          "Business Health",
