          businessEVA,
          {
            nopat: scenario.ebitda * (1 - 0.25),
            capitalCharge: businessCapitalInvested * (scenario.wacc / 100),
            calculation: `${scenario.ebitda} × (1 - 0.25) - (${businessCapitalInvested} × ${scenario.wacc}%) = ${businessEVA}`
          }
